=ABERTO
  (function() {
    const agora = $now.setZone('America/Sao_Paulo'); 
    const dia = agora.setLocale('pt-BR').toFormat('cccc').toLowerCase(); 
    const hora = agora.hour;
    let status = "FECHADO";

    if (dia === 'sábado' || dia === 'domingo') {
      if (hora >= 9 && hora < 15) {
        status = "ABERTO";
      }
    }
    return status;
  })();
}}
